Brewery.create!([
  {name: "Birdsong Brewing Company", location: "1016 North Davidson Street", description: "Birdsong started in 2011 with just two full-time employees. Since then, we’ve brewed all kinds of exciting beers. Thanks to the overwhelming response, we’ve since purchased a 17,000-square-foot building in the Belmont neighborhood with a new 30-barrel brewhouse and room for up to 1,200 barrels of fermentation capacity.\r\n\r\nOur new taproom opened in April 2015, and we’re gearing up for another record-breaking year. The brewery has become what it is today thanks to hardworking, creative and passionate employees. At Birdsong, #CanDoWillDo is the philosophy behind what we brew, how we think and what we believe.", rating: nil, website: "www.birdsongbrewing.com", neighborhood: "Noda", wifi: "Yes", live_music: "", pet_friendly: "", dining: "", outdoor_seating: "", phone_number: "", food_trucks: "", image_file_name: "birdsong_logo-300x300.jpg", image_content_type: "image/jpeg", image_file_size: 28164, image_updated_at: "2016-08-28 22:17:16"},
  {name: "Free Range Brewing ", location: "2320 North Davidson Street", description: "LET YOUR TASTE BUDS ROAM!!!\r\n\r\nFree Range Brewing is looking to bring something new to your glass. Something locally sourced and inspired. Made with a sense of adventure, purpose and responsibility. We brew small batches of a wide variety of styles to suit most any taste.", rating: nil, website: "www.freerangebrewing.com", neighborhood: "", wifi: "yes", live_music: "yes", pet_friendly: "yes", dining: "no", outdoor_seating: "yes", phone_number: "(980) 201-9096", food_trucks: "yes", image_file_name: "1l-EWGFo.jpg", image_content_type: "image/jpeg", image_file_size: 56060, image_updated_at: "2016-08-28 23:50:00"},
  {name: "Heist Brewery", location: "2909 North Davidson Street", description: " HEIST BREWERY has quickly become one of Charlotte’s greatest destinations for craft beer, creative cocktails, and twisted eats. A Belgian centric brewpub located in the heart of NoDa, the Heist Brewery experience begins the moment you walk through the door. We offer a modern yet rustic atmosphere, where new flavors of beer, wood-fired specialties and contemporary cuisine blend perfectly.", rating: nil, website: "heistbrewery.com", neighborhood: "Noda", wifi: "Yes", live_music: "Yes", pet_friendly: "Yes", dining: "Yes", outdoor_seating: "Yes", phone_number: "(704)-375-8260", food_trucks: "No", image_file_name: "Heist.png", image_content_type: "image/png", image_file_size: 9735, image_updated_at: "2016-08-28 22:18:20"},
  {name: "Leigon Brewing", location: "1906 Commonwealth Ave", description: "Legion Brewing Company was born from the ethos that sharing good beer with friends is one of life’s great pleasures. That spirit of culture is alive in our neighborhood home here in Plaza Midwood. Legion Brewing’s goal is to be a constant uniting force of community and great beer here in Plaza Midwood and the greater Charlotte, NC area.", rating: nil, website: "legionbrewing.com", neighborhood: "Elizabeth", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "", food_trucks: "No", image_file_name: "legion.png", image_content_type: "image/png", image_file_size: 174665, image_updated_at: "2016-08-28 22:18:33"},
  {name: "Lenny Boy Brewing Co.", location: "2224 Hawkings Street", description: "\"Welcome to Lenny Boy Brewing Company. We are proud to be part of Charlotte NC's local brewery scene. We offer unique options including organic kombucha, gluten free wild ales, gluten-reduced beer, sours, and traditional ales & lagers. When you're looking for a great place to grab a drink that anyone can enjoy, stop by Lenny Boy. Our local beers and kombucha offer a unique taste that can only be found at Lenny Boy.\"", rating: nil, website: "discoverlennyboy.com", neighborhood: "South End", wifi: "No", live_music: "No", pet_friendly: "Yes", dining: "No", outdoor_seating: "No", phone_number: "(980)-585-1728", food_trucks: "Yes", image_file_name: "Lenny-Boy-Brewing-Company.png", image_content_type: "image/png", image_file_size: 65655, image_updated_at: "2016-09-07 00:56:35"},
  {name: "Noda Brewing Co.", location: "2229 North Davidson Street", description: "\"NoDa Brewing Company was founded on a culture of homebrewing. In fact, our two chief brew masters cut their teeth in the Carolina Brewmasters, one of the oldest homebrewing clubs in America. Whether it’s ultra small-batch beers or full-production, year-round brews, we approach every batch the same—with the highest standards and the ultimate attention to detail. It’s a sign of our respect for you. We hope you enjoy our beers as much as we have enjoyed making them.  So however you say it, “Cheers!” From NoDa.\"", rating: nil, website: "nodabrewing.com/the-beer", neighborhood: "Noda", wifi: "No", live_music: "No", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "(704)-900-6851", food_trucks: "Yes", image_file_name: "noda1.jpeg", image_content_type: "image/jpeg", image_file_size: 24725, image_updated_at: "2016-09-07 00:57:28"},
  {name: "Rock Bottom Restaurant & Brewery ", location: "401 North Tryon Street Suite 100", description: "\"Passionate about pints. Maniacal for malts. Rock Bottom always has been and always will be about the beer. Every tank we tap represents hours of planning, experimentation, ingenuity and precision by your local Rock Bottom Brewmaster. While we pride ourselves on the remarkably consistent quality of our beers, we're just as dedicated to letting the personality, experience and particular expertise of each Brewmaster shine through.", rating: nil, website: "rockbottomsouth.com", neighborhood: "Fourth Ward", wifi: "Yes", live_music: "No", pet_friendly: "No", dining: "Yes", outdoor_seating: "Yes", phone_number: "(704)-334-2739", food_trucks: "No", image_file_name: "rbb1.png", image_content_type: "image/png", image_file_size: 76788, image_updated_at: "2016-09-07 00:58:50"},
  {name: "Salud Beer Shop", location: "3306-B North Davidson Street", description: "\"Salud Beer Shop is located in the North Davidson (NoDa) neighborhood of Charlotte, NC. We started out as a bottle shop promoting local and regional craft beers. Today, we continue to grow with 12 beers on tap, and hundreds of craft beer bottles and cans from all over the world.\"", rating: nil, website: "saludbeershop.com", neighborhood: "Noda", wifi: "No", live_music: "No", pet_friendly: "No", dining: "No", outdoor_seating: "No", phone_number: "(704)-900-7767", food_trucks: "No", image_file_name: "Salud-Logo.png", image_content_type: "image/png", image_file_size: 559959, image_updated_at: "2016-09-07 00:59:41"},
  {name: "Sugar Creek Brewing Company", location: "215 Southside Drive ", description: " \"Sugar Creek Brewing Company is bringing Belgian-inspired beer to the Carolinas and beyond.  The Sugar Creek Brewing philosophy combines the watchful eye of a Trappist Monk with the technical precision of a nuclear engineer to craft fresh, full-flavored beers that are immensely satisfying and intensely drinkable. Using unique combinations of aromatic yeast strains, domestic and imported hops and award-winning malt varieties, Sugar Creek infuses traditional recipes with modern tastes and techniques to create beer that nourishes both mind and body.\"", rating: nil, website: "sugarcreekbrewing.com", neighborhood: "South End", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "(704)-521-3333", food_trucks: "Yes", image_file_name: "suagrc.png", image_content_type: "image/png", image_file_size: 238136, image_updated_at: "2016-09-07 01:00:54"},
  {name: "Sycamore Brewing ", location: "2161 Hawkins Street", description: "\"Our aim at Sycamore Brewing is simple: use the freshest ingredients possible and brew all-natural, extraordinarily tasty craft beer.", rating: nil, website: "sycamorebrew.com", neighborhood: "South End", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "(704)-910-3821", food_trucks: "Yes", image_file_name: "sycamore.jpeg", image_content_type: "image/jpeg", image_file_size: 22463, image_updated_at: "2016-09-07 01:01:42"},
  {name: "Olde Mecklenburg Brewing", location: "4150 Yancey Road", description: "\"In March 2009, The Olde Mecklenburg Brewery opened its doors, at the time Charlotte’s only local brewery. Charlotteans embraced it with a bear hug (that was the authentic German beer talking) and OMB started to build a dedicated following that’s never really stopped growing. Today, in a new, larger space that features a spectacular eight-acre German-style Biergarten, a state-of-the-art 60-barrel Brewery (largest locally owned craft brewhouse in the state), and a dine-in Brauhaus Charlotte’s oldest brewery continues to grow while staying committed to fresh, local beer. And also offers the best brewery tour in America according to USA Today readers. As John Marrino says, “Not everything can be local, but beer can and should be.” OMB looks forward to providing Charlotte with good times and its freshest beer for many years to come.\"", rating: nil, website: "oldemeckbrew.com", neighborhood: "South end", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "Yes", outdoor_seating: "Yes", phone_number: "(704)-525-5644", food_trucks: "No", image_file_name: "oldemb.png", image_content_type: "image/png", image_file_size: 40562, image_updated_at: "2016-09-07 01:02:50"},
  {name: "Three Spirits Brewery", location: "5046 Old Pineville Road", description: "\"The name Three Spirits stems from my love of Dickens’ A Christmas Carol and my earlier career choice. Since Scrooge is such an interesting character to me, I adopted the image of curmudgeon for our logo. Working swing shifts, weekends and holidays in Emergency Medicine is one thing when you have no children; it’s quite a different thing when you do. Scrooge did not see the importance of family to lifelong happiness until almost the end. My previous profession was sending me down the same path. An unexpected illness with lingering health concerns ultimately sealed the deal to run down a new road.", rating: nil, website: "threespirtsbrewery.com", neighborhood: "Starmount", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "(980)-207-4881", food_trucks: "No", image_file_name: "ThreeSpirits_(1).jpg", image_content_type: "image/jpeg", image_file_size: 25575, image_updated_at: "2016-09-07 01:04:30"},
  {name: "Triple C Brewing Company", location: "2900 Griffith Street", description: "\"A floor-to-ceiling wall of glass separates patrons from the brewmaster at this dog-friendly South End taproom pouring its signature and seasonal IPAs, pale ales, porters and more. Plenty of TVs, lots of seating and Ping-Pong and foosball tables up its hangout quotient, and the modern digs also include a large patio.", rating: nil, website: "triplecbrewing.com", neighborhood: "south end", wifi: "No", live_music: "Yes", pet_friendly: "Yes", dining: "No", outdoor_seating: "Yes", phone_number: "(704)-372-3212", food_trucks: "Yes", image_file_name: "CCC_jpeg.jpg", image_content_type: "image/jpeg", image_file_size: 32796, image_updated_at: "2016-09-07 01:05:32"},
  {name: "Unknown Brewing ", location: "1327 South Mint Street", description: "\"At The Unknown Brewing Company we are more than just a name. A name does not mean you make good beer, and good beer does not have to have a name.\\r\\nOur job is to stand out. We are the open road with no destination. We are your first kiss or your last time jumping out of an airplane,\\r\\nor whatever your wildest adventure is. We are The Unknown.\\r\\nSo if you need us to have a name, make one up. If you make one up we prefer you call us something with adventure, or America, or dragons or something epic.", rating: nil, website: "www.unknownbrewing.com", neighborhood: "south end", wifi: "No", live_music: "No", pet_friendly: "No", dining: "No", outdoor_seating: "No", phone_number: "(980)-237-2628", food_trucks: "No", image_file_name: "___.jpeg", image_content_type: "image/jpeg", image_file_size: 116752, image_updated_at: "2016-09-07 01:06:40"},
  {name: "Wooden Robot Brewery", location: "1440 South Tryon Street", description: "\"The crafted beers offered by Wooden Robot Brewery are inspired by Belgian farmhouse brewing traditions. Historically, farmers brewed beer to quench the thirst ...", rating: nil, website: "woodenrobotbrewery.com", neighborhood: "South End ", wifi: "No", live_music: "No", pet_friendly: "Yes", dining: "Yes", outdoor_seating: "Yes", phone_number: "(980)-819-7875", food_trucks: "Yes", image_file_name: "wrb.jpg", image_content_type: "image/jpeg", image_file_size: 51714, image_updated_at: "2016-09-07 01:08:14"},
 
